import '../styles/App.scss';
import '../styles/page.scss';
import { useState } from 'react';

function App() {

  const handleSubmit=(ev)=> ev.preventDefault();

  const [cont , setCont] =useState(0);

  const handleCont = () =>{ return setCont(cont++);}


  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <label name="button" className="js_contador">Contador: {cont}</label>
           <input type="button" value="incrementar" name="button" onClick={handleCont} />
          <input type="button" value="decrementar" name="button" />
          <input type="button" value="reset" name="button" />
      </form>
    </div>
  );
}

export default App;
