import '../styles/App.scss';
import '../styles/page.scss';
import { useState } from 'react';

function App() {

  const [cont , setCont] =useState(0);

  const handleContador = (ev) => {
    ev.preventDefault();
    return cont++

  }


  return (
    <div className="App">
      <label for="button" className="js_contador">Contador: {cont}</label>
      <input type="button" value="incrementar" name="button" onClick={handleContador} />
      <input type="button" value="decrementar" name="button" />
      <input type="button" value="reset" name="button" />
    </div>
  );
}

export default App;
